import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Account.css';

const Register = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [email, setEmail] = useState('');
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(null);

        try {
            const response = await fetch('http://localhost:8000/api/register/', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ username, password, email }),
            });

            if (response.ok) {
                const data = await response.json();
                console.log('User registered successfully:', data);
                navigate('/account/login'); // Перенаправляем на страницу входа
            } else {
                const errorData = await response.json();
                console.log('Registration error data:', errorData);
                setError(errorData);
            }
        } catch (error) {
            console.error('Error during registration:', error);
            setError({ detail: 'An unexpected error occurred.' });
        }
    };

    return (
        <div className="account-form">
            <h2>Регистрация</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Имя пользователя</label>
                    <input type="text" name="username" value={username} onChange={(e) => setUsername(e.target.value)} required />
                </div>
                <div>
                    <label>Электронная почта</label>
                    <input type="email" name="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
                </div>
                <div>
                    <label>Пароль</label>
                    <input type="password" name="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
                </div>
                <button type="submit">Зарегистрироваться</button>
            </form>
            {error && <div className="error">{error.detail || 'Ошибка регистрации'}</div>}
            <p>
                Уже зарегистрированы? <Link to="/account/login">Войти в аккаунт</Link>
            </p>
        </div>
    );
};

export default Register;
