import React, { useEffect, useState } from 'react';
import './Inspiration.css';

const Inspiration = () => {
    const [images, setImages] = useState([]);

    useEffect(() => {
        const fetchImages = async () => {
            try {
                const response = await fetch('/api/inspiration-images/');
                console.log('Response:', response);

                if (response.ok) {
                    const data = await response.json();
                    console.log('Data:', data);
                    setImages(data);
                } else {
                    console.error('Ошибка при загрузке изображений:', response.statusText);
                    const text = await response.text();
                    console.error('Текст ответа:', text);
                }
            } catch (error) {
                console.error('Ошибка при выполнении запроса:', error);
            }
        };

        fetchImages();
    }, []);

    return (
        <div className='main__content'>
            <h1>Страница о вдохновении</h1>
            <p>Добро пожаловать на страницу о вдохновении!</p>
            <div className='image-gallery'>
                {images.map(image => (
                    <div key={image.id} className='image-item'>
                        <img src={image.image} alt={image.caption} onError={(e) => { console.error('Ошибка загрузки изображения:', e); }} />
                        {image.caption && <p>{image.caption}</p>}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Inspiration;
